function App() {
  const product = {
    title: "Stylish T-Shirt",
    price: "$19.99",
    image: "https://via.placeholder.com/300x200", // fake image for now
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white shadow-lg rounded-2xl overflow-hidden w-80">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-48 object-cover"
        />
        <div className="p-4">
          <h2 className="text-lg font-semibold text-gray-800">
            {product.title}
          </h2>
          <p className="text-blue-600 text-md mt-2">{product.price}</p>
          <button className="mt-4 w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition duration-300">
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
